import java.util.Scanner;

public class BOJ_10950 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner scan=new Scanner(System.in);
		int t=scan.nextInt();
		for (int i = 0; i<t; i++) {
			int A=scan.nextInt();
			int B=scan.nextInt();
			System.out.println(A+B);
		}
		scan.close();
	}

}
